    , UPDATE_DATE
) VALUES(
	#{userId}
	, #{qualificationId}
	,now()
	,now()
)

mapper.delete:
	DELETE 
    FROM 
		RELA_USER_QUALIFICATION
	WHERE 
		USER_ID = #{userId}
		AND QUALIFICATION_ID = #{qualificationId}
